queries.query:
SELECT id
FROM d_message_sequences
WHERE local_channel_id = ${localChannelId}

UPDATE d_message_sequences
SET id = id + 1
WHERE local_channel_id = ${localChannelId}

DELETE FROM d_mcm${localChannelId}
WHERE message_id = ?
AND metadata_id IN (${metaDataIds})

DELETE FROM d_mc${localChannelId}
WHERE message_id = ?
AND metadata_id IN (${metaDataIds})
