queries.query:
SELECT id
FROM d_message_sequences
WHERE local_channel_id = ${localChannelId}

UPDATE d_message_sequences
SET id = id + 1
WHERE local_channel_id = ${localChannelId}

SELECT a.* FROM (
SELECT mm.*, m.server_id, ROW_NUMBER() OVER (ORDER BY m.id) AS rownum FROM d_mm${localChannelId} mm JOIN d_m${localChannelId} m ON m.id = mm.message_id WHERE mm.id = ? AND mm.status = ?
) AS a WHERE a.rownum > ${offset} AND a.rownum <= (${offset} + ${limit})

SELECT a.* FROM (
SELECT mm.*, m.server_id, ROW_NUMBER() OVER (ORDER BY m.id) AS rownum FROM d_mm${localChannelId} mm JOIN d_m${localChannelId} m ON m.id = mm.message_id WHERE mm.id = ? AND mm.status = ? AND m.id >= ? AND m.id <= ?
) AS a WHERE a.rownum > ${offset} AND a.rownum <= (${offset} + ${limit})
