mapper.select:
SELECT m.id AS message_id, m.server_id, m.date_created, m.processed, string_agg(mm.id::character varying, ',') AS metadata_ids

GROUP BY m.id

ORDER BY m.id DESC


	OFFSET ${offset}


	LIMIT ${limit}

mapper.update:
UPDATE reprocessing
SET completed = TRUE
WHERE reprocessing_id = #{reprocessingId}

mapper.select:
SELECT *, ${localChannelId} AS local_channel_id
FROM d_mm${localChannelId}
WHERE message_id = #{messageId}
AND id IN

	#{metaDataId}

SELECT *, ${localChannelId} AS local_channel_id
FROM d_mm${localChannelId}
WHERE message_id = #{messageId}
AND id IN

	#{metaDataId}

SELECT #{metaDataId} AS metadata_id, message_id, 'R' AS content_type, "content", is_encrypted, data_type
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = 0



		AND content_type = 'R'


		AND content_type = 'E'

SELECT *
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = #{metaDataId}
AND content_type = 'P'

SELECT *
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = #{metaDataId}
AND content_type = 'T'

SELECT *
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = #{metaDataId}
AND content_type = 'E'

SELECT *
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = #{metaDataId}
AND content_type = 'S'

SELECT *
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = #{metaDataId}
AND content_type = 'O'

SELECT *
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = #{metaDataId}
AND content_type = 'N'

SELECT *
FROM d_mcm${localChannelId}
WHERE metadata_id = #{metaDataId}
AND message_id = #{messageId}

mapper.sql:
WHERE m.date_created  <  #{dateThreshold}


	AND m.processed = TRUE



	AND NOT EXISTS (
		SELECT 1
		FROM d_mm${localChannelId} mm
		WHERE mm.message_id = m.id
		AND mm.status IN

			#{status}

	)

mapper.select:
SELECT m.* FROM d_m${localChannelId} m
LEFT JOIN d_mm${localChannelId} mm ON mm.message_id = m.id AND mm.status IN
#{status}
WHERE m.date_created < #{dateThreshold}
AND m.processed = TRUE
AND mm.id IS NULL
